mapper.select:
SELECT COUNT(*) as cnt
FROM T_USER
WHERE email = #{email}

SELECT userid, name, passwd, phone , email, birthdate, callphone, zipcode, grade, address1, address2, joindate, userphoto, r FROM( SELECT userid, name, passwd, phone , email, birthdate, callphone, zipcode, grade, address1, address2, joindate, userphoto, rownum r FROM( SELECT userid, name, passwd, phone , email, birthdate, callphone, zipcode, grade, address1, address2, joindate, userphoto FROM T_USER
name LIKE '%' || #{word} || '%'
userid LIKE '%' || #{word} || '%'
email LIKE '%' || #{word} || '%'
ORDER BY userid ASC )
)WHERE r >= #{sno} AND r <= #{eno}

mapper.update:
UPDATE T_USER  
SET name= #{name}, callphone=#{callphone}, email=#{email},  zipcode=#{zipcode} ,  address1=#{address1}, address2=#{address2} 
WHERE userid = #{userid}

